import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import $ from 'jquery';

function importAll(r) {
  return r.keys().map(r);
}
const images = importAll(require.context('./images', false, /\.(png|jpe?g|svg)$/));
const imagesAmount = images.length;

class App extends Component {
  componentDidMount(){
    $('.container').height($('.container').width());
    $(window).resize(function(){
      $('.container').height($('.container').width());
    })
    console.log(images);
  }
  render() {
    return (
      <div className="App">
        <div className="container">
          <Photo id="1"/>
          <Photo id="2"/>
          <Photo id="3"/>
          <Photo id="4"/>
          <div className="item center"></div>
          <Photo id="5"/>
          <Photo id="6"/>
          <Photo id="7"/>
          <Photo id="8"/>
        </div>
      </div>
    );
  }
}

class Photo extends Component {
  componentDidMount(){
    var $this = this;
    setInterval(function(){
      $this.forceUpdate();
    }, Math.floor((Math.random() * 4000)+4000))
  }
  componentDidUpdate(){
    $('.item#'+this.props.id).toggleClass('rotate');
  }
  render() {
    var url = images[Math.floor((Math.random() * imagesAmount))].replace('/static/media', './images');
    var number = url.split('.')[3];
    url = url.replace(number+'.', '');

    var img = {
      backgroundImage: "url("+url+")",
      backgroundSize: "cover",
      backgroundRepeat: "no-repeat",
      backgroundPosition: "center center",
      imageOrientation: "0deg"
    }
    return (
      <div className="item" id={this.props.id} style={img}></div>
    )
  };
}

export default App;
