import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import $ from 'jquery';

function importAll(r) {
  return r.keys().map(r);
}
const images = importAll(require.context('./images', false, /\.(png|jpe?g|svg)$/));
const imagesAmount = images.length;

class App extends Component {
  componentDidMount(){
    $('.container').height($('.container').width());
    $(window).resize(function(){
      $('.container').height($('.container').width());
    })
    console.log(images);
  }
  render() {
    return (
      <div className="App">
        <div className="container">
          <Photo/>
          <Photo/>
          <Photo/>
          <Photo/>
          <div className="item center"></div>
          <Photo/>
          <Photo/>
          <Photo/>
          <Photo/>
        </div>
      </div>
    );
  }
}

function Photo(props) {
  var url = images[Math.floor((Math.random() * imagesAmount))].replace('/static/media', '/images');
  var number = url.split('.')[2];
  url = url.replace(number+'.', '');
  console.log(url);
  var img = {
    backgroundImage: "url("+url+")",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    backgroundPosition: "center center"
  }
  return (
    <div className="item" style={img}></div>
  )
}

export default App;
